import React, { useState } from 'react'
import { Accordion, Collapse } from 'reactstrap'

const ListItem = (prop) => {
    const [open,setOpen]=useState(false)
    const toggle = (id) => {
        if (open === id) {
          setOpen();
        } else {
          setOpen(id);
        }
      };
  return (
    <div onClick={()=>setOpen(state=>!state)}>
        {prop.name}
    </div>
  )
}

export default ListItem
